import React from 'react';

const Player = ({ playerNumber, score, currentScore, isActive }) => {
    return (
        <div className={`player ${isActive ? 'active' : ''}`}>
            <h2>{`Player ${playerNumber}: ${name}`}</h2>
            <p>Global Score: {score} </p>
            <p>Current Score: {currentScore} </p>
        </div>
    );
};

export default Player;
