import React, { useReducer, useEffect, useState, useCallback } from 'react';
import { gameReducer } from './GameReducer';
import Dice from './Dice';
import GameControls from './GameControls';
import PlayerScore from './PlayerScore';

const initialState = {
    status: "PLAYING",
    roundScore: 0,
    winningScore: 100,
    turn: "HUMAN",
    playerScore: { HUMAN: 0, AI: 0 },
    winCount: { HUMAN:0, AI: 0},
    lastRoll: [0, 0],
    doubleSix: false
};

export default function Game() {
    const [gameState, dispatch] = useReducer(gameReducer, initialState);
    const [winningScoreInput, setWinningScoreInput] = useState(100);
    const [audioContext, setAudioContext] = useState(null);
    const [isRolling, setIsRolling] = useState(false);

    useEffect(() => {
        const context = new (window.AudioContext || window.webkitAudioContext)();
        setAudioContext(context);

        // Load game state from local storage on component mount
        const savedGameState = localStorage.getItem('gameState');
        if(savedGameState) {
            const parsedData = JSON.parse(savedGameState);
            dispatch({ type: 'newGame', winningScore: parsedData.winningScore });
        }

        return () => {
            if(context) {
                context.close();
            }
        };
    }, []);

    useEffect(() => {
        // Save game state to local storage whenever it changes
        localStorage.setItem('gameState', JSON.stringify(gameState));
    }, [gameState]);

    useEffect(() => {
        if(gameState.turn === "AI" && gameState.status === "PLAYING") {
            setTimeout(playAITurn, 1000);
        }
    }, [gameState.turn, gameState.status]);

    const rollDice = useCallback(() => {
        const dice1 = Math.floor(Math.random() * 6) + 1;
        const dice2 = Math.floor(Math.random() * 6) + 1;
        setIsRolling(true);
        setTimeout(() => {
            dispatch({ type: 'roll', dices: [dice1, dice2] });
            playSound(audioContext, 'roll');
            setIsRolling(false);
        if(dice1 === 6 && dice2 === 6) {
            setTimeout(() => {
                alert('Double Six! You lose your round score!');
            }, 100);
        }
        }, 1000);
    }, [audioContext]);

    const hold = () => {
        dispatch({ type: 'hold' });
        playSound(audioContext, 'hold');
    };

    const startNewGame = () => {
        dispatch({ type: 'newGame', winningScore: winningScoreInput });
        playSound(audioContext, 'newGame');
    };

    const resetGame = () => {
        dispatch({ type: 'reset', winningScore: winningScoreInput });
        playSound(audioContext, 'reset');
    };

    const playAITurn = () => {
        let continueTurn = true;
        while(continueTurn && gameState.status === "PLAYING") {
            rollDice();
            if(gameState.roundScore >= 20 || Math.random() < 0.3) {
                hold();
                continueTurn = false;
            }
        }
    }
    const playSound = (context, type) => {
        if(!context) return;
        const oscillator = context.createOscillator();
        const gainNode = context.createGain();

        oscillator.connect(gainNode);
        gainNode.connect(context.destination);

        switch(type) {
            case 'roll':
                oscillator.type = 'triangle';
                oscillator.frequency.setValueAtTime(440, context.currentTime);
                gainNode.gain.setValueAtTime(1, context.currentTime);
                gainNode.gain.exponentialRampToValueAtTime(0.001, context.currentTime + 0.5);
                oscillator.start(context.currentTime);
                oscillator.stop(context.currentTime + 0.5);
                break;
            case 'hold':
                oscillator.type = 'sine';
                oscillator.frequency.setValueAtTime(660, context.currentTime);
                gainNode.gain.setValueAtTime(1, context.currentTime);
                gainNode.gain.exponentialRampToValueAtTime(0.001, context.currentTime + 0.3);
                oscillator.start(context.currentTime);
                oscillator.stop(context.currentTime + 0.3);
                break;
            case 'newGame':
            case 'reset':
                oscillator.type = 'square';
                oscillator.frequency.setValueAtTime(523.25, context.currentTime);
                gainNode.gain.setValueAtTime(1, context.currentTime);
                gainNode.gain.exponentialRampToValueAtTime(0.001, context.currentTime + 1);
                oscillator.start(context.currentTime);
                oscillator.stop(context.currentTime + 1);
                break;
            default:
                break;
        }
    };

    return (
        <div className="game-container">
            <h1>Dice Game</h1>
            <div className="score-board">
                <div className={`player ${gameState.turn === '1' ? 'active' : ''}`}>
                <h2>Player 1</h2>
                <p>Score: {gameState.playerScore[1]}</p>
                <p>Player 1 Wins: {gameState.winCount[1]}</p>
                </div>
                <div className={`player ${gameState.turn === '2' ? 'active' : ''}`}>
                    <h2>Player 2</h2>
                    <p>Score: {gameState.playerScore[2]}</p>
                    <p>Player 2 Wins: {gameState.winCount[2]}</p>
                </div>
            </div>
            <div className="game-info">
                <p>Current Round Score: {gameState.roundScore}</p>
                <p>Current Turn: Player {gameState.turn}</p>
                <p>Game Status: {gameState.status}</p>
             </div>
             <div className="dice-container">
                <div className="dice">{gameState.lastRoll[0]}</div>
                <div className="dice">{gameState.lastRoll[1]}</div>
            </div>
            {gameState.doubleSix && (
            <div className="double-six-message">
                Double Six! You lose your round score!
            </div>
            )}
            <div className="controls">
                <button onClick={rollDice} disabled={gameState.status !== "PLAYING"}>Roll Dice</button>
                <button onClick={hold} disabled={gameState.status !== "PLAYING"}>Hold</button>
            </div>
            <div className="new-game">
                <input 
                type="number"
                value={winningScoreInput}
                onChange={(e) => setWinningScoreInput(e.target.value)}
                min="1"
                />
                <button onClick={startNewGame}>New Game</button>
                <button onClick={resetGame}>Reset Game</button>
            </div>
        </div>
    )
}
