import React from 'react';

function GameControls({ onRoll, onHold, onNewGame, onReset, isPlaying }) {
  return (
    <div className="game-controls">
      <button onClick={onRoll} disabled={!isPlaying}>Roll Dice</button>
      <button onClick={onHold} disabled={!isPlaying}>Hold</button>
      <button onClick={onNewGame}>New Game</button>
      <button onClick={onReset}>Reset Game</button>
    </div>
  );
}

export default GameControls;
