import React, { useState } from 'react';
import Dice from './Dice';
import Player from './Player';
import GameOptions from './GameOptions';

const App = () => {
    const [player1, setPlayer1] = useState({ name: 'Player 1', score: 0, currentScore: 0, isActive: true });
    const [player2, setPlayer2] = useState({ name: 'Player 2', score: 0, currentScore: 0, isActive: false });
    const [currentPlayer, setCurrentPlayer] = useState(player1);

    const [dice1, setDice1] = useState(0); // initial dice value
    const [dice2, setDice2] = useState(0);

    const [isGameOver, setIsGameOver] = useState(false); // game over state
    const [winningScore, setWinningScore] = useState(100); // winning score

    const activePlayer = currentPlayer === player1 ? player1 : player2;
    const setActivePlayer = currentPlayer === player1 ? setPlayer1 : setPlayer2;

    const rollDice = () => {
        const dice1 = Math.floor(Math.random() * 6) + 1;
        const dice2 = Math.floor(Math.random() * 6) + 1;
        setDice1(dice1);
        setDice2(dice2);

        if (dice1 === 6 && dice2 === 6) {
            setActivePlayer(prev => ({ ...prev, currentScore: 0 }));
            switchPlayer();
        } else {
            setActivePlayer(prev => ({ ...prev, currentScore: prev.currentScore + getTotalScore() }));
        }
    };

    const getTotalScore = () => {
        return dice1 + dice2;
    };

    const holdScore = () => {
        const nextScore = activePlayer.score + activePlayer.currentScore;

        if (nextScore >= winningScore) {
            alert(`Player ${currentPlayer + 1} wins the game!`);
            setIsGameOver(true);
            resetGame();
        } else {
            setActivePlayer(prev => ({ ...prev, score: nextScore, currentScore: 0 }));
            switchPlayer();
        }
    };

    const switchPlayer = () => {
        setCurrentPlayer(currentPlayer === player1 ? player2 : player1)
    };

    const resetGame = () => {
        setPlayer1({ globalScore: 0, currentScore: 0 });
        setPlayer2({ globalScore: 0, currentScore: 0 });
        setCurrentPlayer(player1);
        setIsGameOver(false);
    };

    return (
        <div className="app">
            <h1>Dice Game</h1>
            <div className="players">
                <Player 
                    playerNumber ={1} 
                    name={player1.name} 
                    score={player1.score} 
                    currentScore={player1.currentScore} 
                    isActive={player1.isActive} />
                <Player 
                    playerNumber ={2} 
                    name={player2.name} 
                    score={player2.score} 
                    currentScore={player2.currentScore} 
                    isActive={player2.isActive} />
            </div>

            <Dice num1={dice1} num2={dice2} />

            <GameOptions onRoll={rollDice} onHold={holdScore} isGameOver={isGameOver} />

            <button onClick={resetGame}>Reset Game</button>

            <div className="input-score">
                <label>Set Winning Score:</label>
                <input type="number" value={winningScore} onChange={e => setWinningScore(parseInt(e.target.value))} />
            </div>
        </div>
    );
}

export default App;
