import React, { useRef, useState } from "react";
import { Game } from "./Game";

export default function App() {
  const game = useRef(null);
  if (!game.current) {
    game.current = new Game(100);
  }
  const gameRef = game.current;

  const [turn, setTurn] = useState(gameRef.turn);
  const [roundScore, setRoundScore] = useState(gameRef.roundScore);
  const [scores, setScores] = useState(gameRef.scores);
  const [status, setStatus] = useState(gameRef.status);

  const [dice, setDice] = useState(null);

  function handleRoll() {
    const dices = gameRef.roll();
    syncGameState
  }

  function handleHold() {
    const status = gameRef.hold();
    syncGameState();
  }

  function syncGameState() {
    setTurn(gameRef.turn);
    setRoundScore(gameRef.roundScore);
    setScores(gameRef.scores);
    setStatus(gameRef.status);
  }

  return (
    <div className="App">
      Turn: {turn}
      <br />
      Round Score: {roundScore}
      <br />
      Status: {status}
      <br />
      Player 1 Score:
      {scores["1"]}
      <br />
      Player 2 Score:
      {scores["2"]}
      <br />
      <button onClick={handleRoll}>Roll</button>
      <button onClick={handleHold}>Hold</button>
      {dice && (
        <div>
          <p>Dice1: {dice[0]}</p>
          <p>Dice2: {dice[1]}</p>
        </div>
      )}
    </div>
  );
}
