import React, { useState } from 'react';
import Dice from './Dice';
import Player from './Player';
import GameOptions from './GameOptions';

const App = () => {
    const [player1, setPlayer1] = useState({ name: 'Player 1', score: 0, currentScore: 0, isActive: true });
    const [player2, setPlayer2] = useState({ name: 'Player 2', score: 0, currentScore: 0, isActive: false });
    const [currentPlayer, setCurrentPlayer] = useState(1);

    const [dice1, setDice1] = useState(0); // initial dice value
    const [dice2, setDice2] = useState(0);

    const [isGameOver, setIsGameOver] = useState(false); // game over state
    const [winningScore, setWinningScore] = useState(100); // winning score

    const activePlayer = currentPlayer === 1 ? player1 : player2;
    const setActivePlayer = currentPlayer === 1 ? setPlayer1 : setPlayer2;

    const rollDice = () => {
        if(isGameOver) return;

        const newDice1 = Math.floor(Math.random() * 6) + 1;
        const newDice2 = Math.floor(Math.random() * 6) + 1;
        setDice1(newDice1);
        setDice2(newDice2);

        if (newDice1 === 6 && newDice2 === 6) {
            setActivePlayer(prev => ({ ...prev, currentScore: 0, isActive: false }));
            switchPlayer();
        } else {
            const newScore = activePlayer.currentPlayer + newDice1 + newDice2;
            setActivePlayer(prev => ({ ...prev, currentScore: newScore }));
        }
    };

    const holdScore = () => {
        if(isGameOver) return;

        const nextScore = activePlayer.score + activePlayer.currentScore;

        if (nextScore >= winningScore) {
            setActivePlayer(prev => ({ ...prev, score: nextScore, isActive: false }));
            setIsGameOver(true);
            alert(`Player ${activePlayer.name} wins the game!`);
        } else {
            setActivePlayer(prev => ({ ...prev, score: nextScore, currentScore: 0 , isActive: false}));
            switchPlayer();
        }
    };

    const switchPlayer = () => {
        setCurrentPlayer(currentPlayer === 1 ? 2 : 1);
        setPlayer1(prev => ({...prev, isActive: !prev.isActive }));
        setPlayer2(prev => ({...prev, isActive: !prev.isActive }));
    };

    const resetGame = () => {
        setPlayer1({ name: 'Player 1' ,score: 0, currentScore: 0, isActive: true });
        setPlayer2({ name: 'Player 2' ,score: 0, currentScore: 0, isActive: false });
        setCurrentPlayer(player1);
        setDice1(0);
        setDice2(0);
        setIsGameOver(false);
    };

    return (
        <div className="app">
            <h1>Dice Game</h1>
            <div className="players">
                <Player 
                    playerNumber ={1} 
                    name={player1.name} 
                    score={player1.score} 
                    currentScore={player1.currentScore} 
                    isActive={player1.isActive} />
                <Player 
                    playerNumber ={2} 
                    name={player2.name} 
                    score={player2.score} 
                    currentScore={player2.currentScore} 
                    isActive={player2.isActive} />
            </div>

            <Dice num1={dice1} num2={dice2} />

            <GameOptions onRoll={rollDice} onHold={holdScore} isGameOver={isGameOver} />

            <button onClick={resetGame}>Reset Game</button>

            <div className="input-score">
                <label>Set Winning Score:</label>
                <input 
                    type="number" 
                    value={winningScore} 
                    onChange={e => setWinningScore(parseInt(e.target.value))} />
            </div>
        </div>
    );
}

export default App;
