import React from 'react';

function PlayerScore({ player, score, wins, isActive }) {
  return (
    <div className={`player-score ${isActive ? 'active' : ''}`}>
      <h2>Player {player}</h2>
      <p>Score: {score}</p>
      <p>Wins: {wins}</p>
    </div>
  );
}

export default PlayerScore;
